alpha = np.linalg.inv(alpha)
alpha = np.dot(alpha, A.T)
alpha = np.dot(alpha, y)

plt.xlabel('x')
plt.ylabel('y')
plt.title("Basic Plotting")
plt.plot(x, y, 'bo', label="Blue Dot")
plt.plot(x, alpha[0] * x + alpha[1], 'r', label="Red Line")
plt.legend(["Blue Dot", "Red Line"])
plt.show()
